POST - create 方法

后端代码
 # schema/create.py
 class CreateInfo(graphene.Mutation):
     class Arguments:
         nickname = graphene.String(required=True)
         weight = graphene.Int()

     info = graphene.Field(InfoType) 
     ok = graphene.Boolean()

     def mutate(self, info, **kwargs):
         print(info.context.user, '==当前用户==')
         # kwargs 是传递参数中的变量
         user = info.context.user
         info = Info(**kwargs, owner=user)
         try:
             info.save()
             ok = True
         except Exception:
             ok = False
         return CreateInfo(ok=ok, info=info)

 class CMutation(object):
     create_info = CreateInfo.Field()

 # 项目下的总 schema.py
 from app.schema.create import CMutation
 class Mutations(CMutation, graphene.ObjectType):
     pass

 schema = graphene.Schema(mutation=Mutations)


传递参数
 mutation createInfoNick {
     createInfo (nickname: "wo", weight: 56) {
         info {
             id,
             ...,
             weight,
             owner,
         },
         ok
     }
 }

update.html.body:
POST - update 方法

后端代码
 class UpdateInfo(graphene.Mutation):
     class Arguments:
         nickname = graphene.String()
         pk = graphene.Int(required=True)

     info = graphene.Field(InfoType)
     ok = graphene.Boolean()

     def mutate(self, info, **kwargs):
         pk = kwargs.get('pk')
         info_obj = Info.objects.get(id=pk)
         if not info_obj:
             return UpdateInfo(ok=False)
         info_obj.__dict__.update(**kwargs)
         info_obj.save()
         ok = True
         return UpdateInfo(ok=ok, info=info_obj)

 class UpdateUser(graphene.Mutation):
     pass

 class UMutation(object):
     update_info = UpdateInfo.Field()

     update_user = UpdateUser.Field()

 # 项目总 schema
 from app.schema.update import UMutation
 from app.schema.create import CMutation

 class Mutations(CMutation, UMutation, graphene.ObjectType):
     pass

 schema = graphene.Schema(mutation=Mutations)


传递参数
 mutation updateInfoNick {
     updateInfo (pk: 1, nickname: "passion") {
         info {
             id,
             nickname
         },
         ok
     }
 }
